header("content-type: application/json");

function repeatedDNASequences($s)
{
    $interval = 10;

    $possible_sequences = allSequences($s, $interval);
    sort($possible_sequences);
    $sequences = array_unique($possible_sequences);

//    return compact("possible_sequences","sequences");

    foreach ($sequences as $key => $sequence) {
        if (!searchArray($sequence, $possible_sequences)) {
            unset($sequences[$key]);
        }
        deleteSequences($possible_sequences, $sequence);
    }

    sort($sequences);
    return $sequences;
}

function deleteSequences(&$possible_sequences, $sequence)
{
    $possible_sequences = array_diff($possible_sequences, [$sequence]);
}

function allSequences($s, $interval)
{
    $sequences = [];
    $length = strlen($s);
    for ($i = 0; $i <= ($length - $interval); $i++) {
        $sequences[] = makeSequence($s, $i, $interval);
    }
    return $sequences;
}

function searchArray($sequence, $sequences)
{
    $count = 0;
    foreach ($sequences as $current_sequence) {
        if ($sequence === $current_sequence) {
            if (++$count > 1) {
                return true;
            }
        }
    }


    return false;
}

function makeSequence($s, $key, $interval)
{
    $sequence = "";
    for ($i = 0; $i < $interval; $i++) {
        if (isset($s[$key++])) {
            $sequence .= $s[$key - 1];
        }
    }
    return $sequence;
}

function searchSequence($s, $sequence, $key, $interval)
{
    $length = strlen($s);
    for ($i = 0; $i < ($length - $interval); $i++) {
        if ($i < $key || $i >= $key + $interval) {
            $current_sequence = makeSequence($s, $i, $interval);
            if ($current_sequence === $sequence) {
                return true;
            }
        }

    }
    return false;
}

$s = "";
echo json_encode(repeatedDNASequences($s));